mapper.update:
UPDATE `wms_ware_sku`
SET stock = stock + #{skuNum}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}

UPDATE `wms_ware_sku`
SET stock_locked = stock_locked + #{count}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}
  AND stock - stock_locked >= #{count}

UPDATE `wms_ware_sku`
SET stock_locked = stock_locked - #{count}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}

mapper.select:
SELECT SUM(stock - stock_locked)
FROM `wms_ware_sku`
WHERE sku_id = #{skuId}

SELECT ware_id, sku_id, stock, stock_locked FROM `wms_ware_sku` WHERE sku_id IN
#{skuId}
AND stock - stock_locked > 0

SELECT SUM(stock - stock_locked)
FROM `wms_ware_sku`
WHERE sku_id = #{skuId}
